declare(strict_types=1);

namespace Zikula\ContentModule\Block\Form\Type;

use Symfony\Component\Form\AbstractType;
use Symfony\Component\Form\Extension\Core\Type\CheckboxType;
use Symfony\Component\Form\Extension\Core\Type\IntegerType;
use Symfony\Component\Form\Extension\Core\Type\TextType;
use Symfony\Component\Form\FormBuilderInterface;

/**
 * Sub pages block form type implementation class.
 */
class SubPagesBlockType extends AbstractType
{
    public function buildForm(FormBuilderInterface $builder, array $options)
    {
        $helpText = /** @Translate */'The maximum amount of items to be shown.'
            . ' '
            . /** @Translate */'Only digits are allowed.'
        ;
        $builder->add('amount', IntegerType::class, [
            'label' => 'Amount:',
            'attr' => [
                'maxlength' => 2,
                /** @Ignore */
                'title' => $helpText
            ],
            /** @Ignore */
            'help' => $helpText,
            'empty_data' => 5
        ]);
        $builder->add('inMenu', CheckboxType::class, [
            'label' => 'Use only pages activated for the menu:',
            'label_attr' => ['class' => 'switch-custom'],
            'required' => false
        ]);
        $builder->add('filter', TextType::class, [
            'label' => 'Filter (expert option):',
            'required' => false,
            'attr' => [
                'maxlength' => 255,
                'title' => 'Example: tbl.age >= 18'
            ],
            'help' => 'Example: <code>tbl.age >= 18</code>',
            'help_html' => true
        ]);
    }

    public function getBlockPrefix()
    {
        return 'zikulacontentmodule_subpagesblock';
    }
}
