declare(strict_types=1);

namespace Zikula\ContentModule\Container\Base;

use Symfony\Component\Routing\RouterInterface;
use Symfony\Contracts\Translation\TranslatorInterface;
use Zikula\Common\Translator\TranslatorTrait;
use Zikula\Core\LinkContainer\LinkContainerInterface;
use Zikula\ExtensionsModule\Api\ApiInterface\VariableApiInterface;
use Zikula\ContentModule\Helper\ControllerHelper;
use Zikula\ContentModule\Helper\PermissionHelper;

/**
 * This is the link container service implementation class.
 */
abstract class AbstractLinkContainer implements LinkContainerInterface
{
    use TranslatorTrait;

    /**
     * @var RouterInterface
     */
    protected $router;

    /**
     * @var VariableApiInterface
     */
    protected $variableApi;

    /**
     * @var ControllerHelper
     */
    protected $controllerHelper;

    /**
     * @var PermissionHelper
     */
    protected $permissionHelper;

    public function __construct(
        TranslatorInterface $translator,
        RouterInterface $router,
        VariableApiInterface $variableApi,
        ControllerHelper $controllerHelper,
        PermissionHelper $permissionHelper
    ) {
        $this->setTranslator($translator);
        $this->router = $router;
        $this->variableApi = $variableApi;
        $this->controllerHelper = $controllerHelper;
        $this->permissionHelper = $permissionHelper;
    }

    public function getLinks(string $type = LinkContainerInterface::TYPE_ADMIN): array
    {
        $contextArgs = ['api' => 'linkContainer', 'action' => 'getLinks'];
        $allowedObjectTypes = $this->controllerHelper->getObjectTypes('api', $contextArgs);

        $permLevel = LinkContainerInterface::TYPE_ADMIN === $type ? ACCESS_ADMIN : ACCESS_READ;

        // Create an array of links to return
        $links = [];

        if (LinkContainerInterface::TYPE_ACCOUNT === $type) {
            if (!$this->permissionHelper->hasPermission(ACCESS_OVERVIEW)) {
                return $links;
            }

            if (true === $this->variableApi->get('ZikulaContentModule', 'linkOwnPagesOnAccountPage', true)) {
                $objectType = 'page';
                if ($this->permissionHelper->hasComponentPermission($objectType, ACCESS_READ)) {
                    $routeArgs = ['own' => 1];
                    $showOnlyOwnEntries = (bool)$this->variableApi->get('ZikulaContentModule', 'pagePrivateMode');
                    if (true === $showOnlyOwnEntries) {
                        $routeArgs = [];
                    }
                    $routeName = 'zikulacontentmodule_' . strtolower($objectType) . '_view';
                    $links[] = [
                        'url' => $this->router->generate($routeName, $routeArgs),
                        'text' => $this->trans('My pages', 'zikulacontentmodule'),
                        'icon' => 'list-alt'
                    ];
                }
            }

            if ($this->permissionHelper->hasPermission(ACCESS_ADMIN)) {
                $links[] = [
                    'url' => $this->router->generate('zikulacontentmodule_page_adminindex'),
                    'text' => $this->trans('Content Backend', 'zikulacontentmodule'),
                    'icon' => 'wrench'
                ];
            }

            return $links;
        }

        $routeArea = LinkContainerInterface::TYPE_ADMIN === $type ? 'admin' : '';
        if (LinkContainerInterface::TYPE_ADMIN === $type) {
            if ($this->permissionHelper->hasPermission(ACCESS_READ)) {
                $links[] = [
                    'url' => $this->router->generate('zikulacontentmodule_page_index'),
                    'text' => $this->trans('Frontend', 'zikulacontentmodule'),
                    'title' => $this->trans('Switch to user area.', 'zikulacontentmodule'),
                    'icon' => 'home'
                ];
            }
        } else {
            if ($this->permissionHelper->hasPermission(ACCESS_ADMIN)) {
                $links[] = [
                    'url' => $this->router->generate('zikulacontentmodule_page_adminindex'),
                    'text' => $this->trans('Backend', 'zikulacontentmodule'),
                    'title' => $this->trans('Switch to administration area.', 'zikulacontentmodule'),
                    'icon' => 'wrench'
                ];
            }
        }
        
        if (
            in_array('page', $allowedObjectTypes, true)
            && $this->permissionHelper->hasComponentPermission('page', $permLevel)
        ) {
            $links[] = [
                'url' => $this->router->generate('zikulacontentmodule_page_' . $routeArea . 'view'),
                'text' => $this->trans('Pages', 'zikulacontentmodule'),
                'title' => $this->trans('Pages list', 'zikulacontentmodule')
            ];
        }
        if ('admin' === $routeArea && $this->permissionHelper->hasPermission(ACCESS_ADMIN)) {
            $links[] = [
                'url' => $this->router->generate('zikulacontentmodule_config_config'),
                'text' => $this->trans('Settings', 'zikulacontentmodule'),
                'title' => $this->trans('Manage settings for this application', 'zikulacontentmodule'),
                'icon' => 'wrench'
            ];
        }

        return $links;
    }

    public function getBundleName(): string
    {
        return 'ZikulaContentModule';
    }
}
