declare(strict_types=1);

namespace Zikula\ContentModule\ContentType\Form\Type;

use Symfony\Component\Form\Extension\Core\Type\ChoiceType;
use Symfony\Component\Form\FormBuilderInterface;
use Symfony\Component\OptionsResolver\OptionsResolver;
use Translation\Extractor\Annotation\Ignore;
use Zikula\BlocksModule\Entity\BlockEntity;
use Zikula\BlocksModule\Entity\RepositoryInterface\BlockRepositoryInterface;
use Zikula\ExtensionsModule\ModuleInterface\Content\Form\Type\AbstractContentFormType;

/**
 * Block form type class.
 */
class BlockType extends AbstractContentFormType
{
    /**
     * @var BlockRepositoryInterface
     */
    protected $blockRepository;

    public function __construct(BlockRepositoryInterface $blockRepository)
    {
        $this->blockRepository = $blockRepository;
    }

    public function buildForm(FormBuilderInterface $builder, array $options)
    {
        $blocks = $this->blockRepository->findAll();

        $choices = [];
        /** @var BlockEntity $block */
        foreach ($blocks as $block) {
            $choices[$block->getModule()->getDisplayName() . ' - ' . $block->getTitle()] = $block->getBid();
        }
        ksort($choices);

        $builder
            ->add('blockId', ChoiceType::class, [
                'label' => 'Block to display:',
                'choices' => /** @Ignore */$choices
            ])
        ;
    }

    public function getBlockPrefix()
    {
        return 'zikulacontentmodule_contenttype_block';
    }

    public function configureOptions(OptionsResolver $resolver)
    {
        parent::configureOptions($resolver);

        $resolver->setDefaults([
            'translation_domain' => 'contentTypes'
        ]);
    }
}
