declare(strict_types=1);

namespace Zikula\ContentModule\ContentType\Form\Type;

use Symfony\Component\Form\Extension\Core\Type\ChoiceType;
use Symfony\Component\Form\Extension\Core\Type\TextareaType;
use Symfony\Component\Form\Extension\Core\Type\UrlType;
use Symfony\Component\Form\FormBuilderInterface;
use Symfony\Component\OptionsResolver\OptionsResolver;
use Zikula\ExtensionsModule\ModuleInterface\Content\ContentTypeInterface;
use Zikula\ExtensionsModule\ModuleInterface\Content\Form\Type\AbstractContentFormType;

/**
 * Vimeo form type class.
 */
class VimeoType extends AbstractContentFormType
{
    public function buildForm(FormBuilderInterface $builder, array $options)
    {
        $context = $options['context'] ?? ContentTypeInterface::CONTEXT_EDIT;
        $builder
            ->add('url', UrlType::class, [
                'label' => 'URL to the video clip:',
                'help' => 'Something like "https://vimeo.com/7504225".'
            ])
            ->add('text', TextareaType::class, [
                'label' => 'Video description:',
                'required' => false
            ])
        ;
        if (ContentTypeInterface::CONTEXT_EDIT !== $context) {
            return;
        }
        $builder
            ->add('displayMode', ChoiceType::class, [
                'label' => 'Display mode:',
                'label_attr' => [
                    'class' => 'radio-inline'
                ],
                'choices' => [
                    'Show video inline' => 'inline',
                    'Show video in modal window' => 'modal'
                ],
                'expanded' => true
            ])
        ;
    }

    public function getBlockPrefix()
    {
        return 'zikulacontentmodule_contenttype_vimeo';
    }

    public function configureOptions(OptionsResolver $resolver)
    {
        parent::configureOptions($resolver);

        $resolver->setDefaults([
            'translation_domain' => 'contentTypes'
        ]);
    }
}
