declare(strict_types=1);

namespace Zikula\ContentModule\Menu\Base;

use Knp\Menu\FactoryInterface;
use Knp\Menu\ItemInterface;
use Zikula\ExtensionsModule\Api\ApiInterface\VariableApiInterface;
use Zikula\MenuModule\ExtensionMenu\ExtensionMenuInterface;
use Zikula\UsersModule\Api\ApiInterface\CurrentUserApiInterface;
use Zikula\ContentModule\Helper\ControllerHelper;
use Zikula\ContentModule\Helper\PermissionHelper;

/**
 * This is the extension menu service base class.
 */
abstract class AbstractExtensionMenu implements ExtensionMenuInterface
{
    /**
     * @var FactoryInterface
     */
    protected $factory;

    /**
     * @var VariableApiInterface
     */
    protected $variableApi;

    /**
     * @var CurrentUserApiInterface
     */
    protected $currentUserApi;

    /**
     * @var ControllerHelper
     */
    protected $controllerHelper;

    /**
     * @var PermissionHelper
     */
    protected $permissionHelper;

    public function __construct(
        FactoryInterface $factory,
        VariableApiInterface $variableApi,
        CurrentUserApiInterface $currentUserApi,
        ControllerHelper $controllerHelper,
        PermissionHelper $permissionHelper
    ) {
        $this->factory = $factory;
        $this->variableApi = $variableApi;
        $this->currentUserApi = $currentUserApi;
        $this->controllerHelper = $controllerHelper;
        $this->permissionHelper = $permissionHelper;
    }

    public function get(string $type = self::TYPE_ADMIN): ?ItemInterface
    {
        $contextArgs = ['api' => 'extensionMenu', 'action' => 'get'];
        $allowedObjectTypes = $this->controllerHelper->getObjectTypes('api', $contextArgs);

        $permLevel = self::TYPE_ADMIN === $type ? ACCESS_ADMIN : ACCESS_READ;

        $menu = $this->factory->createItem('zikulacontentmodule' . ucfirst($type) . 'Menu');

        if (self::TYPE_ACCOUNT === $type) {
            if (!$this->currentUserApi->isLoggedIn()) {
                return null;
            }
            if (!$this->permissionHelper->hasPermission(ACCESS_OVERVIEW)) {
                return null;
            }

            if (true === $this->variableApi->get('ZikulaContentModule', 'linkOwnPagesOnAccountPage', true)) {
                $objectType = 'page';
                if ($this->permissionHelper->hasComponentPermission($objectType, ACCESS_READ)) {
                    $routeParameters = ['own' => 1];
                    $showOnlyOwnEntries = (bool) $this->variableApi->get('ZikulaContentModule', 'pagePrivateMode');
                    if (true === $showOnlyOwnEntries) {
                        $routeParameters = [];
                    }
                    $menu->addChild('My pages', [
                        'route' => 'zikulacontentmodule_' . mb_strtolower($objectType) . '_view',
                        'routeParameters' => $routeParameters,
                    ])
                        ->setAttribute('icon', 'fas fa-list-alt')
                        ->setExtra('translation_domain', 'page')
                    ;
                }
            }

            if ($this->permissionHelper->hasPermission(ACCESS_ADMIN)) {
                $menu->addChild('Content Backend', [
                    'route' => 'zikulacontentmodule_page_adminindex',
                ])
                    ->setAttribute('icon', 'fas fa-wrench')
                ;
            }

            return 0 === $menu->count() ? null : $menu;
        }

        $routeArea = self::TYPE_ADMIN === $type ? 'admin' : '';
        if (self::TYPE_ADMIN === $type) {
            if ($this->permissionHelper->hasPermission(ACCESS_READ)) {
                $menu->addChild('Frontend', [
                    'route' => 'zikulacontentmodule_page_index',
                ])
                    ->setAttribute('icon', 'fas fa-home')
                    ->setLinkAttribute('title', 'Switch to user area.')
                ;
            }
        } else {
            if ($this->permissionHelper->hasPermission(ACCESS_ADMIN)) {
                $menu->addChild('Backend', [
                    'route' => 'zikulacontentmodule_page_adminindex',
                ])
                    ->setAttribute('icon', 'fas fa-wrench')
                    ->setLinkAttribute('title', 'Switch to administration area.')
                ;
            }
        }
        
        if (
            in_array('page', $allowedObjectTypes, true)
            && $this->permissionHelper->hasComponentPermission('page', $permLevel)
        ) {
            $menu->addChild('Pages', [
                'route' => 'zikulacontentmodule_page_' . $routeArea . 'view',
            ])
                ->setLinkAttribute('title', 'Pages list')
                ->setExtra('translation_domain', 'page')
            ;
        }
        if ('admin' === $routeArea && $this->permissionHelper->hasPermission(ACCESS_ADMIN)) {
            $menu->addChild('Settings', [
                'route' => 'zikulacontentmodule_config_config',
            ])
                ->setAttribute('icon', 'fas fa-wrench')
                ->setLinkAttribute('title', 'Manage settings for this application')
                ->setExtra('translation_domain', 'config')
            ;
        }

        return 0 === $menu->count() ? null : $menu;
    }

    public function getBundleName(): string
    {
        return 'ZikulaContentModule';
    }
}
