declare(strict_types=1);

namespace Zikula\ContentModule\Routing;

use Symfony\Component\HttpKernel\CacheWarmer\WarmableInterface;
use Symfony\Component\Routing\RequestContext;
use Symfony\Component\Routing\RouterInterface;

class CustomRouter implements RouterInterface, WarmableInterface
{
    /**
     * @var RouterInterface
     */
    private $decoratedRouter;

    public function __construct(RouterInterface $decoratedRouter)
    {
        $this->decoratedRouter = $decoratedRouter;
    }

    public function generate($name, $parameters = [], $referenceType = self::ABSOLUTE_PATH)
    {
        $this->decoratedRouter->setOption('generator_class', 'Zikula\ContentModule\Routing\Generator\CustomUrlGenerator');

        return $this->decoratedRouter->generate($name, $parameters, $referenceType);
    }

    public function setContext(RequestContext $context)
    {
        $this->decoratedRouter->setContext($context);
    }

    public function getContext()
    {
        return $this->decoratedRouter->getContext();
    }

    public function getRouteCollection()
    {
        return $this->decoratedRouter->getRouteCollection();
    }

    public function match($pathinfo)
    {
        return $this->decoratedRouter->match($pathinfo);
    }

    public function warmUp(string $cacheDir)
    {
        return [];
    }
}
